/* 상단 배너 */
.brand-banner {
  width: 100%;
  height: 400px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  color: #fff;
  position: relative;
}

.brand-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* 배너 텍스트 */
.brand-banner-text {
  position: absolute;
  width: 100%;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: white;
  z-index: 1;
  display: flex;
  flex-direction: column;
  justify-content: end;
}

.line-bar {
  width: 70px;
  margin: 30px auto;
  height: 2px;
  background-color: #353535;
}

.brand-banner-text span:nth-child(1) {
  font-size: 40px;
}

.brand-banner-text span:nth-child(3) {
  font-size: 24px;
  padding-bottom: 10px;
}

/* 메뉴 버튼 */
.brand-menu {
  display: flex;
  justify-content: center;
  margin: 20px 0;
}

.menu-btn {
  padding: 10px 20px;
  border: none;
  background-color: #f0f0f0;
  color: #353535;
  font-size: 1rem;
  cursor: pointer;
  margin: 0 10px;
  transition: background-color 0.3s ease;
}

.menu-btn.active {
  background-color: #353535;
  color: #fff;
}

.menu-btn:hover {
  background-color: #e0e0e0;
}

/* 콘텐츠 영역 */
.brand-content {
  display: none;
  padding: 0 20px;
}

.brand-content:not(.hidden) {
  display: block;
}

.intro-section,
.history-section {
  display: flex;
  align-items: center;
  margin: 20px 0;
  gap: 20px;
}

.intro-text,
.history-section {
  flex: 1;
}

.intro-image img {
  width: 100%;
  border-radius: 10px;
}

/* 지도 */
.map {
  margin-top: 40px;
  text-align: center;
}

/* 상세페이지 */
.container {
  max-width: 1200px;
  margin: 20px auto;
  padding: 20px;
  background: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  display: flex;
  gap: 20px;
  flex-wrap: wrap;
}

.product-container {
  display: flex;
  justify-content: space-between;
  width: 100%;
  gap: 10%;
}

/* 상품 이미지 섹션 */
.product-image-section {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 10px;
  max-width: 600px;
}

.product-image-wrapper {
  position: relative;
  overflow: hidden;
  width: 100%;
  aspect-ratio: 1 / 1.5;
}

.product-image {
  width: 100%;
  height: auto;
  object-fit: cover;
}

.thumbnail-wrapper {
  display: flex;
  gap: 10px;
  margin-top: 10px;
}

.thumbnail {
  width: 80px;
  height: 80px;
  border: 2px solid transparent;
  border-radius: 4px;
  object-fit: cover;
  cursor: pointer;
  transition: all 0.3s ease;
}

.thumbnail.active,
.thumbnail:hover {
  transform: scale(1.1);
}

/* 상품 정보 섹션 */
.product-info-section {
  flex: 1;
  max-width: 500px;
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.product-quantity {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
}

.product-quantity label {
  font-size: 14px;
}

.product-quantity input {
  width: 60px;
  height: 30px;
  font-size: 14px;
  text-align: center;
  border: 1px solid #ddd;
  border-radius: 4px;
}

.product-total {
  font-size: 16px;
  font-weight: bold;
  color: #333;
}

.product-buttons {
  display: flex;
  gap: 10px;
  margin-top: 20px;
}

.product-buttons button {
  flex: 1;
  padding: 10px 20px;
  font-size: 16px;
  font-weight: bold;
  text-align: center;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.product-col {
  display: flex;
  width: 100%;
  align-items: center;
}

.product-col span {
  font-size: 16px;
  color: #353535;
}

.product-col span:nth-child(1) {
  min-width: 120px;
}

.product-col:nth-child(2) span {
  color: #008bcc;
  font-size: 12px;
  font-weight: bold;
}

.line-bar-product {
  width: 100%;
  height: 1px;
  background-color: #353535;
}

.min-total {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.min-total span {
  font-size: 12px;
}

.min-total span:nth-child(2) {
  color: #f76560;
}

.product-buttons .buy-now {
  background-color: #000;
  color: #fff;
}

.product-buttons .cart,
.product-buttons .wish {
  background-color: #f1f1f1;
  color: #333;
}

.product-buttons .buy-now:hover {
  background-color: #333;
}

.product-buttons .cart:hover,
.product-buttons .wish:hover {
  background-color: #ddd;
}

.upload-flex {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  gap: 20px;
}

.label-result-section {
  margin-top: 20px;
  gap: 15px;
}

.label-upload {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 20px;
  height: 100px;
  width: 100%;
}

.label-upload input[type="file"] {
  display: none;
}

.upload-label {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 10px;
}

.upload-ui {
  aspect-ratio: 1/1;
  background-color: #d0d0d09c;
  border-radius: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 4px;
  cursor: pointer;
  width: 100px;
  background-size: cover; /* Ensures the image covers the container */
  background-position: center; /* Centers the image */
  background-repeat: no-repeat;
}

#upload-icon {
  display: flex;
}

#result-text {
  font-weight: bold;
  font-size: 14px;
}

#upload-text {
  font-weight: bold;
  font-size: 12px;
}

.upload-ui img {
  width: 32px;
  height: 32px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.generate {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  gap: 10px;
}

.generate-btn {
  background-color: #000;
  color: #fff;
  border: none;
  padding: 10px;
  width: 150px;
  cursor: pointer;
  border-radius: 4px;
}

.meta-style img {
  display: flex;
  width: 150px;
  height: auto;
}

.generate-btn:hover {
  background-color: #333;
}

.result-image {
  background-color: #d0d0d09c;
  border-radius: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  width: 270px;
  height: auto;
  min-height: 300px;
}

#generated-image {
  object-fit: contain; /* 이미지 비율 유지 */
  border-radius: 20px; /* 모서리 둥글게 */
}

/* 스크롤 메뉴 */
.product-scroll {
  width: 100%;
  display: flex;
  flex-direction: column;
}

.scroll-menu {
  margin: 100px 0;
  display: flex;
  justify-content: space-around;
  gap: 10px;
}

.scroll-menu button {
  padding: 10px 20px;
  border: none;
  background-color: #fff;
  cursor: pointer;
  transition: background-color 0.3s ease;
  border: 1px solid #eee;
  flex: 1;
}

.product-main {
  display: flex;
  flex-direction: column;
  gap: 20px;
  align-items: center;
}

#detail-section,
#info-section,
#review-section,
#qa-section {
  scroll-margin-top: 250px;
}

.detail-image {
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 20px;
}

.detail-image img {
  width: 100%;
  max-width: 500px;
  height: auto;
  object-fit: cover;
}

.info-container {
  display: flex;
  justify-content: space-between;
  gap: 20px;
  padding: 20px 0;
}

.info-column {
  flex: 1;
  font-size: 12px;
}

.info-column h3 {
  margin-bottom: 10px;
  font-weight: bold;
}

.info-column p,
.info-column ul {
  line-height: 1.6;
  margin: 5px 0;
}

.info-column ul {
  padding-left: 20px;
  list-style-type: disc;
}

.divider {
  width: 1px;
  background-color: #ddd;
  margin: 0 20px;
}

/* 로딩 스피너 스타일 */
.loading-spinner {
  width: 40px;
  height: 40px;
  border: 5px solid #f3f3f3;
  border-top: 5px solid #3498db;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

#product-page {
  min-height: 100vh;
}

@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

/* 반응형 디자인 */
@media (max-width: 768px) {
  .container {
    flex-direction: column;
    padding: 10px;
  }

  .product-image-section,
  .product-info-section {
    max-width: 100%;
  }

  .thumbnail-wrapper {
    justify-content: center;
  }
  .product-container {
    flex-direction: column;
    gap: 20px;
  }
  .info-column {
    font-size: 9px;
  }
}
